import React from "react";
import { logo } from "./logo.svg";

export default class TaskItem extends React.Component {
  constructor(props, context) {
    super(props, context);
    this.state = {
      deleted: false
    };
  }

  handleComplete = e => {
    e.preventDefault();
    this.props.handleComplete(this.props.task);
  };

  render() {
    // console.log("task item props:", this.props);

    const inlineStyle = {
      display: "inline-flex"
    };

    return (
      <div className="taskItemContainer">
        <div style={inlineStyle}>
          <img src={academicIcon} alt={this.props.task.category} />
          <li className="taskItem">{this.props.task.details}</li>
          <button onClick={this.handleComplete} className="delete">
            COMPLETE
          </button>
        </div>
      </div>
    );
  }
}
