import React from "react";
import academicIcon from "./academic.svg";
import careerIcon from "./career.svg";
import healthIcon from "./health.svg";

export default class TaskItem extends React.Component {
  constructor(props, context) {
    super(props, context);
    this.state = {
      deleted: false
    };
  }

  handleComplete = e => {
    e.preventDefault();
    this.props.handleComplete(this.props.task);
  };

  renderIcon(params) {
    switch (params) {
      case "academic":
        return academicIcon;
        break;
      case "career":
        return careerIcon;
        break;
      case "health":
        return healthIcon;
        break;

      default:
        break;
    }
  }

  render() {
    // console.log("task item props:", this.props);

    const inlineStyle = {
      display: "inline-flex"
    };

    const iconStyle = {
      height: "25px",
      width: "auto",
      marginRight: "2em"
    };

    return (
      <div className="taskItemContainer">
        <div style={inlineStyle}>
          <p>{this.props.task.category}</p>
          <img
            style={iconStyle}
            src={academicIcon}
            alt={this.props.task.category}
          />
          <li className="taskItem">{this.props.task.details}</li>
          <button onClick={this.handleComplete} className="delete">
            COMPLETE
          </button>
        </div>
      </div>
    );
  }
}
