import React from "react";
import TaskForm from "./TaskForm";
import Header from "./Header.js";
import TaskListContainer from "./TaskListContainer";
import "./App.css";

class App extends React.Component {
  state = {
    taskCollection: []
  };

  handleSubmit = task => {
    this.setState(
      {
        taskCollection: [...this.state.taskCollection, task]
      },
      () => {
        console.log(this.state);
      }
    );
  };

  handleComplete = task => {
    console.log("clicked on", task);
    const newTaskCollection = this.state.taskCollection.filter(
      taskObj => taskObj !== task
    );
    console.log(this.state.taskCollection);

    console.log("new task collection:", newTaskCollection);

    // this.setState({
    //   taskCollection: newTaskCollection
    // });
  };

  render() {
    // console.log(this.state.taskCollection);

    return (
      <div className="App">
        <Header />
        <TaskForm handleSubmit={this.handleSubmit} />
        <TaskListContainer
          handleComplete={this.handleComplete}
          taskCollection={this.state.taskCollection}
        />
      </div>
    );
  }
}

export default App;
