import React from "react";

export default class TaskForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      tasksCollection: [],
      task: ""
    };
  }

  handleSubmit = e => {
    e.preventDefault();
    const data = this.state;
    const newCollection = [...this.state.tasksCollection, data];
    this.setState({
      tasksCollection: newCollection
    });
  };

  otherHandleSubmit = () => {
    this.props.handleSubmit(this.props.task);
  };

  handleChange = e => {
    this.setState({
      [e.target.name]: e.target.value
    });
  };

  render() {
    return (
      <div className="formContainer">
        <h1>Add a Task!</h1>
        <form onSubmit={this.handleSubmit}>
          <input
            name="task"
            className="inputField"
            action="post"
            placeholder="Task Details"
            onChange={this.handleChange}
            value={this.state.task}
          />
          <button onClick={this.outherHandleSubmit} className="addButton">
            ADD
          </button>
        </form>
      </div>
    );
  }
}
