  // };

  // otherHandleSubmit = () => {
  //   this.props.handleSubmit(this.props.task);
  //   console.log(this.props.task);
  // };

  handleChange = e => {
    this.setState({
      [e.target.name]: e.target.value
    });
  };

  render() {
    return (
      <div className="formContainer">
        <h1>Add a Task!</h1>
        <form onSubmit={this.handleSubmit}>
          <input
            name="task"
            className="inputField"
            action="post"
            placeholder="Task Details"
            onChange={this.handleChange}
            value={this.state.task}
          />
          <button onClick={this.otherHandleSubmit} className="addButton">
            ADD
          </button>
        </form>
      </div>
    );
  }
}
