import React from "react";

export default class TaskForm extends React.Component {
  constructor(props, context) {
    super(props, context);
    this.state = {
      tasksCollection: [],
      task: ""
    };
  }

  // handleClick = e => {
  //   e.preventDefault();
  //   console.log("state:", this.state.task);
  // };

  handleSubmit = e => {
    e.preventDefault();
    const data = this.state;
    const newCollection = [...this.state.tasksCollection, data];
    this.setState({
      tasksCollection: newCollection
    });
    console.log("data object:", data);
    console.log("data collection:", this.state);
  };

  handleChange = e => {
    this.setState({
      [e.target.name]: e.target.value
    });
  };

  // componentDidMount() {}

  render() {
    const { inputData } = this.state;
    return (
      <div className="formContainer">
        <h1>Add a Task!</h1>
        <p>{inputData}</p>
        <form onSubmit={this.handleSubmit}>
          <input
            name="task"
            className="inputField"
            action="post"
            placeholder="Task Details"
            onChange={this.handleChange}
            value={this.state.task}
          />
          <button onClick={this.handleClick} className="addButton">
            ADD
          </button>
        </form>
      </div>
    );
  }
}
